mapper.update:
UPDATE
    MEMBER
SET
    name = #{name}
,   id = #{id}
,   password = #{password}
,       email = #{email}
,       phone_number = #{phoneNumber}
,       point = #{point}
,       profile_image = #{profileImage}
,       created_date = #{createdDate}
,       updated_date = #{updatedDate}
,       status = #{status}
WHERE
	member_id = #{memberId}
